targets.expr:
sort_desc(
max by (agentPoolID, agentPoolName) (
  azure_devops_agentpool_info
  * on (agentPoolID) group_left() (
    count by(agentPoolID) (
      azure_devops_agentpool_agent_info
{enabled="true",status="online"}
    )
  )
)
)

sort_desc(
max by (agentPoolID, agentPoolName) (
  azure_devops_agentpool_info
  * on (agentPoolID) group_left() (
    count by(agentPoolID) (
      azure_devops_agentpool_agent_info
{enabled="true",status="online"}
    )
  )
)
)

label_replace(
  ( 
    azure_devops_agentpool_agent_job{definitionName!="PoolMaintenance"}
    * on (agentPoolAgentID) group_left(agentPoolID,agentPoolAgentName) azure_devops_agentpool_agent_info
    * on (agentPoolID) group_left(agentPoolName) (azure_devops_agentpool_info)
  )
  , "projectID", "$1", "scopeID", "^(.+)$"
)
* on (projectID) group_left(projectName) (azure_devops_project_info)

* 1000

sum by (agentPoolName, agentPoolID) (
  azure_devops_agentpool_info
  * on (agentPoolID) group_left() (
    sum by(agentPoolID) (
      azure_devops_agentpool_agent_info
      * on (agentPoolAgentID) group_left() (
        count by (agentPoolAgentID) (azure_devops_agentpool_agent_job{definitionName!="PoolMaintenance"})
      )
    )
  )
)

sum(
  sum by(projectID, buildDefinitionID) (
    increase(azure_devops_stats_project_builds_duration_count[31d])
  )
  * on(projectID) group_left(projectName) (azure_devops_project_info{projectName="$Project"})
)

sum(
  sum by(projectID, buildDefinitionID) (
    increase(azure_devops_stats_project_builds_duration_count[31d])
  )
  * on(projectID) group_left(projectName) (azure_devops_project_info{projectName="$Project"})
) / 31

sum(
  sum by(projectID, buildDefinitionID) (
    increase(azure_devops_stats_project_builds_duration_count{result="succeeded"}[31d])
  )
  * on(projectID) group_left(projectName) (azure_devops_project_info{projectName="$Project"})
)
/
sum(
  sum by(projectID, buildDefinitionID) (
    increase(azure_devops_stats_project_builds_duration_count[31d])
  )
  * on(projectID) group_left(projectName) (azure_devops_project_info{projectName="$Project"})
)

time() - 
(
  ( 
    azure_devops_pullrequest_info{isDraft="false"}
    * on (projectID) group_left(projectName) azure_devops_project_info{projectName="$Project"}
  )
  * on (repositoryID) group_left(repositoryName) azure_devops_repository_info
  * on (pullrequestID) group_left(job) azure_devops_pullrequest_status{type="created"}
)

topk(
  5,
  azure_devops_repository_stats{type="size"}
  * on(projectID) group_left(projectName) (azure_devops_project_info{projectName="$Project"})
  * on(repositoryID) group_left(repositoryName) (azure_devops_repository_info)
)
